import { ChangeDetectorRef, Component, Injectable, NgZone } from '@angular/core';
import { JwtHelperService } from '@auth0/angular-jwt';
import { AuthService } from 'src/app/auth/auth.service';
import { RoleEnum } from 'src/app/models/userEnums.model';
import { UserGetDTO } from 'src/app/models/userGetDTO.model';
import { UserService } from 'src/app/user.service';
import { environment } from 'src/env/env';

@Injectable()
@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  constructor(private authService: AuthService) {}
  user:UserGetDTO;
  role: RoleEnum ;
 // private cdr: ChangeDetectorRef;
  
  
  ngOnInit(): void {
 
  
    this.authService.userState.subscribe((result) => {
      if(result != null){
        this.role = result.role;
      }else{
       this.role=RoleEnum.UNAUTHENTICATED;
      }
     // this.cdr.detectChanges();
    })
  }

  logout(): void {
    console.log("LOG OUT ")
    this.authService.logout();
  }

  isLoggedIn(): boolean {
    return this.authService.isLoggedIn();
  }

  isAdmin(): boolean {
    console.log("USAO U IS ADM")
    console.log('ROLA JE ', this.role)
 
    return this.authService.getRole() == RoleEnum.ADMIN;
  }

  isGuest(): boolean {
    return this.authService.getRole() == RoleEnum.GUEST;
  }

  isOwner(): boolean {
    return this.authService.getRole() == RoleEnum.OWNER;
  }
  

 }
 



<mat-toolbar>
  <!-- <img src="../../assets/images/logo_color.png" alt="Logo" class="logo"> -->
  <span class="logo-text">Explore, watch, enjoy!</span>
  <div>
    <button *ngIf="isLoggedIn()" [routerLink]="['myNotifications']" style="float: right" mat-raised-button color="">
      <mat-icon>notifications</mat-icon>
    </button>
    <button *ngIf="role == 'ADMIN'" [routerLink]="['adminRatingRequests']"  style="float: right" mat-raised-button color="">Rating requests</button>
    <button *ngIf="role == 'OWNER'" [routerLink]="['ownersAccommodations']"  style="float: right" mat-raised-button color="">My Accommodations</button>
    <button *ngIf="role == 'GUEST'" [routerLink]="['guestsReservations']"  style="float: right" mat-raised-button color="">My Reservations</button>
    <button  *ngIf="role == 'ADMIN'" [routerLink]="['accommodationsRequests']"  style="float: right" mat-raised-button color="">Accommodation requests</button>
    <!--<button *ngIf="role == 'GUEST'" [routerLink]="['editReservation']"  style="float: right" mat-raised-button color="">Edit reservation</button> -->
    <!--<button *ngIf="role == 'GUEST'"   [routerLink]="['createReservation']"  style="float: right" mat-raised-button color="">Create reservation</button> -->
    <!-- <button *ngIf="role == 'OWNER'" [routerLink]="['editAccommodation']"  style="float: right" mat-raised-button color="">Edit Accommodation</button> -->
    <button *ngIf="role == 'OWNER'" [routerLink]="['createAccommodation']"  style="float: right" mat-raised-button color="">Create Accommodation</button>
    <button *ngIf="role == 'ADMIN'" [routerLink]="['users-view']"  style="float: right" mat-raised-button color="">View users</button>
    <button *ngIf="isLoggedIn()" [routerLink]="['editAccount']"  style="float: right" mat-raised-button color="">Edit Account</button>
    <button *ngIf="!isLoggedIn()" [routerLink]="['register']" style="float: right" mat-raised-button color="">Register</button>
    <button *ngIf="!isLoggedIn()" [routerLink]="['login']" style="float: right" mat-raised-button color="">Log in</button>
    <button [routerLink]="['home']"  style="float: right" mat-raised-button color="">Home</button>
    <button  *ngIf="isLoggedIn()" [routerLink]="['home']" (click)="logout()" style="float: right" mat-raised-button color="">Log out</button>
    <button  *ngIf="role == 'OWNER'"  [routerLink]="['ownerRatings']" style="float: right" mat-raised-button color="">My reviews</button>
    <button  *ngIf="role == 'OWNER'"  [routerLink]="['userReports']" style="float: right" mat-raised-button color="">Report guest</button>
    <button  *ngIf="role == 'GUEST'"  [routerLink]="['userReports']" style="float: right" mat-raised-button color="">Report owner</button>
    <button *ngIf="role=='GUEST'" [routerLink]="['favouriteAccommodations']" style="float: right" mat-raised-button color="">Favourite accommodations</button>
    <button *ngIf="role=='OWNER'" [routerLink]="['ownerReport']" style="float: right;" mat-raised-button color="">Owner reports</button>
  </div>
</mat-toolbar>
