import { ChangeDetectorRef, Component } from '@angular/core';
import {FormControl, Validators, FormsModule, ReactiveFormsModule, FormGroup, FormBuilder, AbstractControl, ValidationErrors} from '@angular/forms';
import {MatInputModule} from '@angular/material/input';
import {MatFormFieldModule} from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { Router } from '@angular/router';
import {MatButtonModule} from '@angular/material/button';
import {MatSelectChange, MatSelectModule} from '@angular/material/select';
import { User } from 'src/app/models/user.model';
import { UserService } from 'src/app/user.service';
import { UserPostDTO } from 'src/app/models/userPostDTO.model';
import { RoleEnum } from 'src/app/models/userEnums.model';
import {MatSlideToggleChange, MatSlideToggleModule} from '@angular/material/slide-toggle';
import { MatOptionSelectionChange, ThemePalette } from '@angular/material/core';
import { CommonModule } from '@angular/common';
import { MatDatepicker, MatDatepickerModule } from '@angular/material/datepicker';
import { MatDatepickerActions } from '@angular/material/datepicker';


@Component({
  selector: 'app-register-form',
  templateUrl: './register-form.component.html',
  styleUrls: ['./register-form.component.css'],
  standalone: true,
    imports: [MatFormFieldModule, MatInputModule, FormsModule, ReactiveFormsModule,MatIconModule,MatButtonModule,MatSelectModule,
      MatSlideToggleModule,CommonModule,MatDatepickerModule]
})
export class RegisterFormComponent {

  color: string = 'primary'; // Theme color for slide toggles
  checked: boolean = false; // Default checked state for slide toggles
  disabled: boolean = false; // Default disabled state for slide toggles
  hide: boolean = true; // Hide/show password toggle

  
   passwordMatchValidator: Validators = (control: AbstractControl): ValidationErrors | null => {
    const password = control.get('password')?.value;
    const confirmPassword = control.get('confirmPassword')?.value;
  
    return password === confirmPassword ? null : { passwordMismatch: true };
  };

  // Form group definition with form controls and initial state
  createRegisterForm = new FormGroup({
    name: new FormControl('', Validators.required),
    surname: new FormControl('', Validators.required),
    username: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', Validators.required),
    confirmPassword: new FormControl('', Validators.required),
    birthdate: new FormControl('', Validators.required),
    role: new FormControl('', Validators.required),
    requestNotification: new FormControl(false), // Default value set to false
    cancellationNotification: new FormControl(false), // Default value set to false
    ownerRatingNotification: new FormControl(false), // Default value set to false
    accommodationRatingNotification: new FormControl(false), // Default value set to false
    ownerRepliedNotification: new FormControl(false) // Default value set to false
  });

  constructor(private cdr: ChangeDetectorRef, private userService: UserService, private router: Router) { }

  // Method to navigate to home page
  navigateToHome() {
    this.router.navigate(['home']);
  }

  // Method to handle registration
  register() {
    // Enable button indicator or loader
    // this.button_enabled = true; // You can uncomment this if you have this logic

    // Constructing UserPostDTO object from form values
    const user: UserPostDTO = {
      firstName: this.createRegisterForm.value.name,
      lastName: this.createRegisterForm.value.surname,
      username: this.createRegisterForm.value.username,
      password: this.createRegisterForm.value.password,
     // passwordConfirmation: this.createRegisterForm.value.confirmPassword,
      birthdate: this.createRegisterForm.value.birthdate,
      email: this.createRegisterForm.value.username
      // role: this.createRegisterForm.value.role as RoleEnum,
      // reservationRequestNotification: this.createRegisterForm.value.requestNotification,
      // reservationCancellationNotification: this.createRegisterForm.value.cancellationNotification,
      // ownerRatingNotification: this.createRegisterForm.value.ownerRatingNotification,
      // accommodationRatingNotification: this.createRegisterForm.value.accommodationRatingNotification,
      // ownerRepliedToRequestNotification: this.createRegisterForm.value.ownerRepliedNotification,
      // deleted: false // Assuming this is a default value
    };

    // Calling the service method to register the user
    this.userService.registerUser(user).subscribe({
      next: (data: UserPostDTO) => {
        // Successful registration navigation logic
        console.log('User registered successfully:', data);
        this.navigateToHome(); // Example navigation to home page after registration
      },
      error: (error) => {
        // Error handling logic, if needed
        console.error('Error occurred during registration:', error);
        // Handle error feedback to the user, e.g., display error message
      }
    });

    // Optionally, reset the form after successful registration
    // this.createRegisterForm.reset(); // Uncomment if needed
  }

  // Method to handle slide toggle change for request notification
  onSlideToggleChangeRequestNotification(event: MatSlideToggleChange) {
    this.createRegisterForm.patchValue({ requestNotification: event.checked });
  }

  // Method to handle slide toggle change for cancellation notification
  onSlideToggleChangeCancellationNotification(event: MatSlideToggleChange) {
    this.createRegisterForm.patchValue({ cancellationNotification: event.checked });
  }

  // Method to handle slide toggle change for owner rating notification
  onSlideToggleChangeOwnerRatingNotification(event: MatSlideToggleChange) {
    this.createRegisterForm.patchValue({ ownerRatingNotification: event.checked });
  }

  // Method to handle slide toggle change for accommodation rating notification
  onSlideToggleChangeAccommodationRatingNotification(event: MatSlideToggleChange) {
    this.createRegisterForm.patchValue({ accommodationRatingNotification: event.checked });
  }

  // Method to handle slide toggle change for owner replied notification
  onSlideToggleChangeOwnerRepliedRequestNotification(event: MatSlideToggleChange) {
    this.createRegisterForm.patchValue({ ownerRepliedNotification: event.checked });
  }

  // Method to handle role selection change
  onRoleSelectionChange(event: MatSelectChange) {
    // Example implementation based on selected role
    const selectedRole = event.value;
    if (selectedRole === 'ADMIN') {
      // Disable certain notifications for ADMIN role
      this.createRegisterForm.get('requestNotification')?.disable();
      this.createRegisterForm.get('cancellationNotification')?.disable();
      this.createRegisterForm.get('ownerRatingNotification')?.disable();
      this.createRegisterForm.get('accommodationRatingNotification')?.disable();
      this.createRegisterForm.get('ownerRepliedNotification')?.disable();
    } else if (selectedRole === 'GUEST') {
      // Enable specific notifications for GUEST role
      this.createRegisterForm.get('requestNotification')?.disable();
      this.createRegisterForm.get('cancellationNotification')?.disable();
      this.createRegisterForm.get('ownerRatingNotification')?.disable();
      this.createRegisterForm.get('accommodationRatingNotification')?.disable();
      this.createRegisterForm.get('ownerRepliedNotification')?.enable();
    } else if (selectedRole === 'OWNER') {
      // Enable certain notifications for OWNER role
      this.createRegisterForm.get('requestNotification')?.enable();
      this.createRegisterForm.get('cancellationNotification')?.enable();
      this.createRegisterForm.get('ownerRatingNotification')?.enable();
      this.createRegisterForm.get('accommodationRatingNotification')?.enable();
      this.createRegisterForm.get('ownerRepliedNotification')?.disable();
    }
  }

