export class EditAccommodationComponent {
  constructor(route, router, snackBar, cdr, fb, accommodationService, priceCardService, reservationService, dialog) {
    this.route = route;
    this.router = router;
    this.snackBar = snackBar;
    this.cdr = cdr;
    this.fb = fb;
    this.accommodationService = accommodationService;
    this.priceCardService = priceCardService;
    this.reservationService = reservationService;
    this.dialog = dialog;
    this.AccommodationTypeEnum = AccommodationTypeEnum;
    this.ReservationConfirmationEnum = ReservationConfirmationEnum;
    this.accommodationTypeEnum = AccommodationTypeEnum;
    this.images = [];
    this.imageDataList = [];
    this.result = true;
    this.dataSource = new MatTableDataSource([]);
