import { Component, Input } from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { Router } from '@angular/router';
import { AccommodationDetails } from 'src/app/accommodation/accommodation/model/accommodationDetails.model';

@Component({
  selector: 'app-searched-accommodation',
  templateUrl: './searched-accommodation.component.html',
  styleUrls: ['./searched-accommodation.component.css'],
  standalone:true,
  imports:[MatCardModule]
})
export class SearchedAccommodationComponent {
  @Input()
  searchedAccommodation:AccommodationDetails;
  constructor(private router: Router) {}

  showDetails() {
    this.router.navigate(['/accommodations', this.searchedAccommodation.accommodation.id]);
  }
}
<mat-card class="custom-card">
    <mat-card-content>
      <h2>{{searchedAccommodation.accommodation.name}}</h2>
      <p>{{searchedAccommodation.accommodation.location.country}}, {{searchedAccommodation.accommodation.location.city}}</p>
      <img src="{{searchedAccommodation.accommodation.images}}">
      <br>
      <p>Total price:{{searchedAccommodation.totalPrice}}</p>
      <p>Unit price:{{searchedAccommodation.unitPrice}}</p>
      <p>Average rating:{{searchedAccommodation.averageRating}}</p>
      <button (click)="showDetails()">Show details</button>
    </mat-card-content>
  </mat-card>
